const groupTransactionsByDate = (transactions: AllDataProps[]) => {
  return transactions.reduce((groups, transaction) => {
    const date = new Date(transaction.transDate).toISOString().split('T')[0]
    if (!groups[date]) {
      groups[date] = []
    }
    groups[date].push(transaction)
    return groups
  }, {} as Record<string, AllDataProps[]>)
}

const home = () => {
  const db = useSQLiteContext();

  const [expenses, setExpenses] = useState<AllDataProps[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [currency, setCurrency] = React.useState('USD');
  const [totalAmount, setTotalAmount] = useState<{ total: number, transDate: string }[]>([]);
  const theme = useTheme();
  const globalStyles = Styles();
  const colorScheme = useColorScheme();

  const zoom = useSharedValue(0);

  function openZoom() {
    zoom.value = withTiming(zoom.value + height / 16, { duration: 500 });
    setTimeout(() => {
      router.push('/CreateExpensePage');
    }, 100)
  }

  const { height } = Dimensions.get('window');

  useFocusEffect(
    useCallback(() => {
      try {
        zoom.value = 0;
        setIsLoading(true);
        processRecurringTransactions().then((result) => {
          fetchTransactions();
        }
        )

        // Check for recurring transactions that need to be created
      } catch (err) {
        console.log(err);
        fetchTransactions(); // Fetch transactions even if checking recurring fails
      }
      // Currency fetch remains the same
      (async function currency() {
        const mycurrency = await getItemAsync('currency')
        if (mycurrency) {
          setCurrency(mycurrency)
        }
      })()
    }, [])
  );

  // Function to fetch transactions
  const fetchTransactions = async () => {
    try {
      const data = await db.getAllAsync<AllDataProps>(`
        SELECT *
        FROM transactions as t 
        INNER JOIN budgets as b ON t.bdgtId = b.bdgtId 
        INNER JOIN categories as c ON t.categId = c.categId 
        ORDER BY transDate DESC;`);

      if (data) {
        setExpenses(data);
      }

      const totalAmount2 = await db.getAllAsync<{ total: number, transDate: string }>(`
        SELECT type, sum(amount) as total, SUBSTR(transDate, 1, 10) as transDate FROM transactions GROUP BY SUBSTR(transDate, 1, 10), type HAVING type = 'Expense' AND userId = 1
      `);

      if (totalAmount2) {
        setTotalAmount(totalAmount2.map(item => ({
          total: item.total,
          transDate: item.transDate
        })));
      }
    } catch (err) {
      console.log(err);
    } finally {
      setIsLoading(false);
    }
  };

  async function processRecurringTransactions(): Promise<boolean> {
    try {
      const now = new Date();

      // Get all recurring transactions
      const recurringTransactions = await db.getAllAsync<Transaction>(
        `SELECT * FROM transactions WHERE userId = 1 AND transRecurrence != 'Once-0'`
      );

      if (recurringTransactions.length === 0) {
        return true;
      }

      // Process each recurring transaction
      for (const transaction of recurringTransactions) {
        await processTransaction(transaction, now);
      }
      return true;
    } catch (error) {
      console.error("Error processing recurring transactions:", error);
      return false;
    }
  }

  async function processTransaction(transaction: Transaction, now: Date): Promise<void> {
    const [recurrenceType, recurrenceData] = transaction.transRecurrence.split('-');

    // Find the last created instance of this recurring transaction
    const lastInstance = await db.getFirstAsync<Transaction>(
      `SELECT * FROM transactions 
     WHERE userId = ? 
     AND description = ? 
     AND categId = ? 
     AND bdgtId = ? 
     AND type = ? 
     AND transRecurrence = 'Once-0' 
     ORDER BY transDate DESC 
     LIMIT 1`,
      [
        1,
        transaction.description,
        transaction.categId,
        transaction.bdgtId,
        transaction.type
      ]
    );

    // Calculate the start date to check for missed instances
    let startDate: Date;
    if (!lastInstance) {
      // If no instance found, use the original transaction creation date
      startDate = new Date(transaction.transCreatedAt!);
    } else {
      // Start from the day after the last instance
      startDate = new Date(lastInstance.transDate);
      startDate.setDate(startDate.getDate() + 1);
    }

    // Get all dates between the start date and today where the transaction should have occurred
    const datesToProcess = getDatesBetween(startDate, now, recurrenceType, recurrenceData);

    // Create transactions for all missed dates
    for (const date of datesToProcess) {
      await createRecurringTransaction(transaction, date);
    }
  }

  function getDatesBetween(startDate: Date, endDate: Date, recurrenceType: string, recurrenceData: string): Date[] {
    const dates: Date[] = [];
    const currentDate = new Date(startDate);

    // Process each day between start date and end date (inclusive)
    while (currentDate <= endDate) {
      if (shouldCreateOnDate(currentDate, recurrenceType, recurrenceData)) {
        dates.push(new Date(currentDate));
      }
      // Move to the next day
      currentDate.setDate(currentDate.getDate() + 1);
    }

    return dates;
  }

  function shouldCreateOnDate(date: Date, recurrenceType: string, recurrenceData: string): boolean {
    if (recurrenceType === "Weekly") {
      // Weekly recurrence - check if the day of week is in the recurring days
      const recurringDays = recurrenceData.split('');
      const currentDay = date.getDay().toString();
      return recurringDays.includes(currentDay);
    }
    else if (recurrenceType === "Monthly") {
      // Monthly recurrence
      if (recurrenceData === "1" && date.getDate() === 1) {
        return true;
      } else if (recurrenceData === "2" && date.getDate() === 2) {
        return true;
      } else if (recurrenceData === "3" && date.getDate() === 3) {
        return true;
      } else if (recurrenceData === "4" && date.getDate() === 4) {
        return true;
      } else if (recurrenceData === "last") {
        // Check if it's the last day of the month
        const lastDayOfMonth = new Date(date.getFullYear(), date.getMonth() + 1, 0).getDate();
        return date.getDate() === lastDayOfMonth;
      } else if (recurrenceData.includes("custom-")) {
        // Custom day of month
        const customDay = parseInt(recurrenceData.split('-')[1]);
        return date.getDate() === customDay;
      } else {
        return date.getDate() === parseInt(recurrenceData);
      }
    }

    return false;
  }

  async function createRecurringTransaction(transaction: Transaction, date: Date): Promise<void> {
    try {
      // Check if transaction already exists for this date
      const existingTransaction = await db.getFirstAsync<Transaction>(
        `SELECT * FROM transactions 
       WHERE userId = ? 
       AND description = ? 
       AND amount = ? 
       AND categId = ? 
       AND bdgtId = ? 
       AND type = ? 
       AND transRecurrence = 'Once-0' 
       AND date(transDate) = date(?)`,
        [
          1,
          transaction.description,
          transaction.amount,
          transaction.categId,
          transaction.bdgtId,
          transaction.type,
          date.toISOString().split('T')[0]
        ]
      );

      if (existingTransaction) {
        return;
      }

      // Get the associated budget
      const selectedBudget = await db.getFirstAsync<Budget>(
        `SELECT * FROM budgets WHERE bdgtId = ? AND endDate >= ? AND userId = ?`,
        [transaction.bdgtId, date.toISOString().split('T')[0], 1]
      );

      if (!selectedBudget) {
        return;
      }

      // Format time part from the original transaction
      const timePart = transaction.transDate.toString().split(' ')[1] || "00:00:00";

      // Create the new transaction
      await db.runAsync(
        `INSERT INTO transactions 
       (userId, type, amount, description, transDate, transCreatedAt, transRecurrence, categId, bdgtId) 
       VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?)`,
        [
          1,
          transaction.type,
          transaction.amount,
          transaction.description,
          date.toISOString().split('T')[0] + " " + timePart,
          new Date().toISOString(), // Current timestamp for creation
          "Once-0",
          transaction.categId,
          transaction.bdgtId
        ]
      );

      // Update the budget balance
      await db.runAsync(
        `UPDATE budgets SET bdgtCapacity = ? WHERE bdgtId = ? AND userId = ?`,
        [
          transaction.type === "Expense" ?
            selectedBudget.bdgtCapacity - transaction.amount :
            selectedBudget.bdgtCapacity + transaction.amount,
          selectedBudget.bdgtId!,
          1
        ]
      );

    } catch (error) {
      throw error;
    }
  }

  useEffect(() => {
    (async function validate() {
      const redirectKey = await getItemAsync('usersuccessfullystarted');
      const currentUser = await getItemAsync('currentUserId');
      if (!redirectKey || !currentUser || redirectKey !== 'true' || currentUser !== '1') {
        router.replace('/OnboardPage');
      }
    })()
  }, []);



  const groupedExpenses = groupTransactionsByDate(expenses);

  return (
    <React.Fragment>
      <Appbar.Header mode='medium' style={{ height: 144 }} >
        <Appbar.Content title={`Finnote`} titleStyle={{ fontWeight: '700', fontSize: 48, lineHeight: 56 }} />
      </Appbar.Header>
      <View style={[globalStyles["container"], { backgroundColor: colorScheme == 'dark' ? theme.colors.background : "#f4f5f7" }]}>

        <View style={{ flex: 1 }}>
          <ScrollView>
            {!isLoading ?
              expenses.length === 0 ?
                <View>
                  <Illustration color={theme.colors.onBackground} />
                  <Text style={styles.title}>Start your journey by recording what you spent today.</Text>
                  <Button mode='contained' onPress={() => router.push("/CreateExpensePage")} style={globalStyles["btn"]} labelStyle={globalStyles["btn?"].label}>Add an expense or income</Button>

                </View> :
                <View style={{ flex: 1, height: '100%', marginBottom: 32 }}>
                  {Object.keys(groupedExpenses).map((date) => (
                    <List.Section key={date} title={(new Date().toISOString().split('T')[0] === date ? 'Today' : new Date(date).toLocaleDateString())} titleStyle={{ fontWeight: '700', paddingLeft: 12, color: theme.colors.onBackground }}>
                      <Text style={{ fontWeight: '700', color: theme.colors.outline, position: 'absolute', right: 0, top: 16 }}>{currency + " " + (totalAmount.find(k => k.transDate === date.toString())?.total || 0) + " spent"}</Text>
                      <View style={globalStyles["section"]}>
                        {groupedExpenses[date].map((item, index) => {
                          return (
                            <ListItem
                              text={item.categName}
                              desc={item.description}
                              customkey={index}
                              key={item.transId?.toString()!}
                              func={() => router.push({ pathname: '/expenseDetails/[exp]', params: { exp: item.transId ? item.transId.toString() : '' } })}
                              leftComponent={<View style={{ width: 42, height: 42, borderRadius: 100, marginLeft: 20, backgroundColor: ((item.categColor === "#37352f" || item.categColor === "#f3f5f7") ? colorScheme === 'dark' ? "#f3f5f7" : "#37352f" : item.categColor) || theme.colors.elevation.level3, justifyContent: 'center', alignItems: 'center' }}><List.Icon color={theme.colors.background} icon={item.categIcon || 'record'} /></View>}
                              rightComponent={<Text style={styles.amountLabel}>{(item.type === "Expense" ? "- " : "+ ") + currency + " " + item.amount?.toString()}</Text>}
                            />
                          )
                        })}
                      </View>
                    </List.Section>
                  ))}
                </View>
              :
              <View style={{ flex: 1, height: '100%', marginBottom: 32 }}>
                <View style={globalStyles["section"]}>
                  {Array.from("123").map((item, i) => {
                    return (
                      <ListItem
                        text={""}
                        customkey={i.toString()}
                        key={item.toString()}
                        func={() => { }}
                        customStyle={{ backgroundColor: `#f4f5f712` }}
                      />
                    )
                  })}

                </View>
                <View style={{ height: 40 }}></View>
                <View style={globalStyles["section"]}>
                  {Array.from("12").map((item, i) => {
                    return (
                      <ListItem
                        text={""}
                        customkey={i.toString()}
                        key={item.toString()}
                        func={() => { }}
                        customStyle={{ backgroundColor: `#f4f5f712` }}
                      />
                    )
                  })}

                </View>
                <View style={{ height: 40 }}></View>
                <View style={globalStyles["section"]}>
                  {Array.from("12").map((item, i) => {
                    return (
                      <ListItem
                        text={""}
                        customkey={i.toString()}
                        key={item.toString()}
                        func={() => { }}
                        customStyle={{ backgroundColor: `#f4f5f712` }}
                      />
                    )
                  })}

                </View>
              </View>
            }
          </ScrollView>
        </View>
        <Animated.View style={{ width: 42, height: 42, zIndex: 1000, pointerEvents: 'none', transform: [{ scale: zoom }], ...globalStyles["fab"], borderRadius: 700 }}></Animated.View>
        <FAB
          visible={expenses.length !== 0 && !isLoading}
          mode='elevated'
          size='medium'
          icon={'plus'}
          style={globalStyles["fab"]}
          color={globalStyles["fab?"].icon.color}
          onPress={() => openZoom()}
        />
      </View>
    </React.Fragment>
  )
}

const styles = StyleSheet.create({
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 8,
    textAlign: 'center'
  },
  amountLabel: { fontWeight: '700', fontSize: 18, alignSelf: 'center' }
})

export default home